import React from "react";
import clouds from "../src/assets/clouds.png";
import CountrySelector from "./components/CountrySelector";

const api = {
  key: "d58aba219971924715b5ebbfde38970d",
  base: "https://api.openweathermap.org/data/2.5/",
};

function App() {
  const days = (e) => {
    let days = [
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
      "Sunday",
    ];

    let day= days[e.getDay()];
  };

  return (
    <div className="app">
      <main>
        <div className="d-flex flex-row">
          <div className="search_box">
            <img src={clouds} className="cloud" />
            <CountrySelector className="country_selector" />
            <input
              type="text"
              className="search_bar"
              placeholder="Please enter your location..."
            />
          </div>
          <div className="location_box">Novi Sad, Serbia</div>
          <div className="days">{days(new day())}</div>
        </div>
      </main>
    </div>
  );
}

export default App;
