import React, { useState } from "react";
import clouds from "../src/assets/clouds.png";
import CountrySelector from "../src/components/CountrySelector";
import WeatherResult from "../src/components/WeatherResult";

const api = {
  key: "d58aba219971924715b5ebbfde38970d",
  base: "https://api.openweathermap.org/data/2.5/",
};

const App = (props) => {
  const { showModal, setShowModal } = props;
  const [showWeatherResult, setShowWeatherResult] = useState(false);

  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});

  const search = w => {
    if (w.key === "Enter") {
      fetch (`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
      .then(res => res.json())
      .then(result => {
        setQuery('');
        setWeather(result)});
    }
  }

  // const handleSubmit = (e) => {
  //   e.preventDefault();
  //   console.log("enter");
  // };

  return (
    <div className="app">
      <main>
        <div className="d-flex flex-row">
          <div className="search_box">
            <img src={clouds} className="cloud" />
            {/* <form onSubmit={handleSubmit} > */}
              <CountrySelector className="country_selector" />
              <input
                type="text"
                className="search_bar"
                placeholder="Please enter your location..."
                onChange={e => setQuery(e.target.value)}
                value={query}
                onKeyPress={search}
              />
              {/* <input type="submit" />
            </form> */}
          </div>
        </div>
        <div>
          <div className="location_box">
              <div className="location">{weather.name},{weather.sys.country}</div>
              <div className="date"></div>
          </div>
          <div className="weather_box">
              <div>
                {Math.round(weather.main.temp)}c
              </div>

          </div>
        </div>
        {/* {showWeatherResult && (
          <WeatherResult
            showModal={showWeatherResult}
            setShowModal={setShowWeatherResult}
          />
        )}
        <button onClick={() => setShowWeatherResult(true)}>show result</button> */}
      </main>
    </div>
  );
};

export default App;
