import React, { useState } from "react";

const api = {
  key: "d58aba219971924715b5ebbfde38970d",
  base: "https://api.openweathermap.org/data/2.5/",
};

//https://api.openweathermap.org/data/2.5/onecall?lat=33.44&lon=-94.04&exclude=daily&appid=d58aba219971924715b5ebbfde38970d

const ForecastWeather = () => {
  const [query, setQuery] = useState("");
  const [weather, setWeather] = useState({});

  const handleSubmit = (lat, lon) => {
    fetch(
      `${api.base}onecall?lat=${lat}&lon=${lon}&exclude=daily&appid=${api.key}`
    )
      .then((res) => res.json())

      .then((result) => {
        setQuery("");
        setWeather(result);
        console.log(result);
      });
  };
  return <div> Weekly Forecast</div>;
};

export default ForecastWeather;
