import React, { useState } from "react";
import clouds from "../src/assets/clouds.png";

const api = {
  key: "d58aba219971924715b5ebbfde38970d",
  base: "https://api.openweathermap.org/data/2.5/",
};

const App = () => {
  const [query, setQuery] = useState("");
  const [weather, setWeather] = useState({});
  const [weeklyForecast, setWeeklyForecast] = useState({});

  //api.openweathermap.org/data/2.5/forecast/daily?q=${query}&cnt={cnt}&appid={api.key}
  //https://api.openweathermap.org/data/2.5/onecall?lat=33.44&lon=-94.04&exclude=daily&appid=d58aba219971924715b5ebbfde38970d
  //https://api.openweathermap.org/data/2.5/forecast?q=belgrade&appid=d58aba219971924715b5ebbfde38970d

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(`${api.base}forecast?q=${query}&exclude=daily&appid=${api.key}`)
      .then((res) => res.json())
      // .then((res) => {
      //   fetch(
      //     `${api.base}onecall?lat={lat}&lon={lon}&exclude=daily&appid=${api.key}`
      //   ).then((res) => setWeeklyForecast());
      // })
      .then((result) => {
        setQuery("");
        setWeather(result);
        console.log(result);
      });
  };

  const dateBuilder = (d) => {
    let months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "Oct",
      "November",
      "December",
    ];
    let days = [
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
      "Sunday",
    ];

    let day = days[d.getDay()];
    let month = months[d.getMonth()];
    let date = d.getDate();

    return `${month} ${day} ${date} `;
  };

  return (
    <div className="app_results">
      <main className="main">
        <div className="search_box">
          <img src={clouds} className="cloud" alt="" />
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              className="search_bar"
              placeholder="Please enter your location..."
              onChange={(e) => setQuery(e.target.value)}
              value={query}
            />
          </form>
        </div>

        <div>
          <div className="location_box">
            <div className="location">{weather.name}</div>
            <div className="date">{dateBuilder(new Date())}</div>
          </div>
          <div className="weather_box">
            <div className="temp">{weather.main && weather.main.temp} 25°C</div>
          </div>
        </div>
        <div className="weekly_forecast">
          <div>
            <div className="date">{dateBuilder(new Date("day"))}</div>
            <div className="weather_box">
              <div className="temp">{weather.main && weather.main.temp}°C</div>
            </div>
          </div>
          <div>
            <div className="date">{dateBuilder(new Date())}</div>
            <div className="weather_box">
              <div className="temp">{weather.main && weather.main.temp}°C</div>
            </div>
          </div>
          <div>
            <div className="date">{dateBuilder(new Date())}</div>
            <div className="weather_box">
              <div className="temp">{weather.main && weather.main.temp}°C</div>
            </div>
          </div>
          <div>
            <div className="date">{dateBuilder(new Date())}</div>
            <div className="weather_box">
              <div className="temp">{weather.main && weather.main.temp}°C</div>
            </div>
          </div>
          <div>
            <div className="date">{dateBuilder(new Date())}</div>
            <div className="weather_box">
              <div className="temp">{weather.main && weather.main.temp}°C</div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default App;
